    dependsOn("testDebugUnitTest")

    reports {
        xml.required.set(true)
        html.required.set(true)
        csv.required.set(false)
    }

    val fileFilter = listOf(
        "**/R.class",
        "**/R$*.class",
        "**/BuildConfig.*",
        "**/Manifest*.*",
        "**/*Test*.*",
        "android/**/*.*",
        "**/*\$ViewInjector*.*",
        "**/*\$ViewBinder*.*",
        "**/databinding/*",
        "**/generated/callback/*"
    )

    val debugTree = fileTree("${project.buildDir}/tmp/kotlin-classes/debug") {
        exclude(fileFilter)
    }

    val mainSrc = "${project.projectDir}/src/main/java"

    sourceDirectories.setFrom(files(mainSrc))
    classDirectories.setFrom(files(debugTree))
    executionData.setFrom(fileTree(project.buildDir) {
        include("jacoco/testDebugUnitTest.exec")
    })
}

package appserviciotecnico.model.domain.usecases

import appserviciotecnico.model.data.repository.SolicitudRepository

/**
 * Caso de uso: Eliminar solicitud de servicio
 * Encapsula la lógica de negocio para eliminar una solicitud
 */
class EliminarSolicitudUseCase(
    private val repository: SolicitudRepository
) {

    suspend fun execute(solicitudId: Long): Result<Unit> {
        return try {
            if (solicitudId <= 0) {
                return Result.failure(Exception("ID de solicitud inválido"))
            }

            repository.eliminarSolicitudPorId(solicitudId)
            Result.success(Unit)

        } catch (e: Exception) {
            Result.failure(e)
        }
    }
}

package appserviciotecnico.network.api

import appserviciotecnico.network.models.CotizacionDTO
import okhttp3.ResponseBody
import retrofit2.Response
import retrofit2.http.*

/**
 * API Service: Endpoints del microservicio de Cotizaciones
 */
interface CotizacionApi {

    @GET("cotizaciones")
    suspend fun obtenerCotizaciones(): Response<List<CotizacionDTO>>

    @GET("cotizaciones/{id}")
    suspend fun obtenerPorId(@Path("id") id: Long): Response<CotizacionDTO>

    @POST("cotizaciones")
    suspend fun crearCotizacion(@Body cotizacion: CotizacionDTO): Response<CotizacionDTO>

    @PUT("cotizaciones/{id}")
    suspend fun actualizarCotizacion(
        @Path("id") id: Long,
        @Body cotizacion: CotizacionDTO
    ): Response<CotizacionDTO>

    @DELETE("cotizaciones/{id}")
    suspend fun eliminarCotizacion(@Path("id") id: Long): Response<ResponseBody>

    @GET("cotizaciones/estado/{estado}")
    suspend fun obtenerPorEstado(@Path("estado") estado: String): Response<List<CotizacionDTO>>
}

package appserviciotecnico.network.api

import appserviciotecnico.network.models.SolicitudDTO
import okhttp3.ResponseBody
import retrofit2.Response
import retrofit2.http.*

/**
 * API Service: Endpoints del microservicio de Solicitudes
 * Consume los endpoints REST del backend Spring Boot
 */
interface SolicitudApi {

    /**
     * GET /api/solicitudes
     * Obtener todas las solicitudes
     */
    @GET("solicitudes")
    suspend fun obtenerSolicitudes(): Response<List<SolicitudDTO>>

    /**
     * GET /api/solicitudes/{id}
     * Obtener una solicitud por ID
     */
    @GET("solicitudes/{id}")
    suspend fun obtenerPorId(@Path("id") id: Long): Response<SolicitudDTO>

    /**
     * POST /api/solicitudes
     * Crear nueva solicitud
     */
    @POST("solicitudes")
    suspend fun crearSolicitud(@Body solicitud: SolicitudDTO): Response<SolicitudDTO>

    /**
     * PUT /api/solicitudes/{id}
     * Actualizar solicitud existente
     */
    @PUT("solicitudes/{id}")
    suspend fun actualizarSolicitud(
        @Path("id") id: Long,
        @Body solicitud: SolicitudDTO
    ): Response<SolicitudDTO>

    /**
     * DELETE /api/solicitudes/{id}
     * Eliminar solicitud
     */
    @DELETE("solicitudes/{id}")
    suspend fun eliminarSolicitud(@Path("id") id: Long): Response<ResponseBody>

    /**
     * GET /api/solicitudes/estado/{estado}
     * Obtener solicitudes por estado
     */
    @GET("solicitudes/estado/{estado}")
    suspend fun obtenerPorEstado(@Path("estado") estado: String): Response<List<SolicitudDTO>>
}

package appserviciotecnico.network.models

import com.google.gson.annotations.SerializedName

/**
 * DTO: Solicitud de servicio técnico del backend
 * Representa la respuesta JSON del microservicio Spring Boot
 */
data class SolicitudDTO(
    @SerializedName("id")
    val id: Long? = null,

    @SerializedName("servicio")
    val servicio: String,

    @SerializedName("fechaAgendada")
    val fechaAgendada: String,

    @SerializedName("horaAgendada")
    val horaAgendada: String,

    @SerializedName("estado")
    val estado: String = "PENDIENTE",

    @SerializedName("clienteNombre")
    val clienteNombre: String,

    @SerializedName("descripcion")
    val descripcion: String,

    @SerializedName("categoriaId")
    val categoriaId: Int,

    @SerializedName("fechaCreacion")
    val fechaCreacion: String? = null
)

package appserviciotecnico.network.repository

import android.util.Log
import appserviciotecnico.network.config.RetrofitClient
import appserviciotecnico.network.models.SolicitudDTO
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext

/**
 * Repository: Maneja la comunicación con el backend para Solicitudes
 * Implementa el patrón Repository para separar la lógica de red de la UI
 */
class SolicitudRemoteRepository {

    private val api = RetrofitClient.solicitudApi

    /**
     * Obtener todas las solicitudes del backend
     */
    suspend fun obtenerSolicitudes(): Result<List<SolicitudDTO>> {
        return withContext(Dispatchers.IO) {
            try {
                val response = api.obtenerSolicitudes()

                if (response.isSuccessful) {
                    val solicitudes = response.body() ?: emptyList()
                    Result.success(solicitudes)
                } else {
                    Result.failure(Exception("Error del servidor: ${response.code()} - ${response.message()}"))
                }
            } catch (e: Exception) {
                Log.e("SolicitudRemoteRepo", "Error al obtener solicitudes: ${e.message}", e)
                Result.failure(e)
            }
        }
    }

    /**
     * Obtener una solicitud por ID
     */
    suspend fun obtenerPorId(id: Long): Result<SolicitudDTO> {
        return withContext(Dispatchers.IO) {
            try {
                val response = api.obtenerPorId(id)

                if (response.isSuccessful) {
                    val solicitud = response.body()
                    if (solicitud != null) {
                        Result.success(solicitud)
                    } else {
                        Result.failure(Exception("Solicitud no encontrada"))
                    }
                } else {
                    Result.failure(Exception("Error: ${response.code()}"))
                }
            } catch (e: Exception) {
                Log.e("SolicitudRemoteRepo", "Error al obtener solicitud: ${e.message}", e)
                Result.failure(e)
            }
        }
    }

    /**
     * Crear nueva solicitud en el backend
     */
    suspend fun crearSolicitud(solicitud: SolicitudDTO): Result<SolicitudDTO> {
        return withContext(Dispatchers.IO) {
            try {
                val response = api.crearSolicitud(solicitud)

                if (response.isSuccessful) {
                    val solicitudCreada = response.body()
                    if (solicitudCreada != null) {
                        Result.success(solicitudCreada)
                    } else {
                        Result.failure(Exception("Respuesta vacía del servidor"))
                    }
                } else {
                    Result.failure(Exception("Error al crear: ${response.code()}"))
                }
            } catch (e: Exception) {
                Log.e("SolicitudRemoteRepo", "Error al crear solicitud: ${e.message}", e)
                Result.failure(e)
            }
        }
    }

    /**
     * Actualizar solicitud existente
     */
    suspend fun actualizarSolicitud(id: Long, solicitud: SolicitudDTO): Result<SolicitudDTO> {
        return withContext(Dispatchers.IO) {
            try {
                val response = api.actualizarSolicitud(id, solicitud)

                if (response.isSuccessful) {
                    val actualizada = response.body()
                    if (actualizada != null) {
                        Result.success(actualizada)
                    } else {
                        Result.failure(Exception("Respuesta vacía"))
                    }
                } else {
                    Result.failure(Exception("Error al actualizar: ${response.code()}"))
                }
            } catch (e: Exception) {
                Log.e("SolicitudRemoteRepo", "Error al actualizar: ${e.message}", e)
                Result.failure(e)
            }
        }
    }

    /**
     * Eliminar solicitud
     */
    suspend fun eliminarSolicitud(id: Long): Result<Unit> {
        return withContext(Dispatchers.IO) {
            try {
                val response = api.eliminarSolicitud(id)

                if (response.isSuccessful) {
                    Result.success(Unit)
                } else {
                    Result.failure(Exception("Error al eliminar: ${response.code()}"))
                }
            } catch (e: Exception) {
                Log.e("SolicitudRemoteRepo", "Error al eliminar: ${e.message}", e)
                Result.failure(e)
            }
        }
    }

    /**
     * Obtener solicitudes por estado
     */
    suspend fun obtenerPorEstado(estado: String): Result<List<SolicitudDTO>> {
        return withContext(Dispatchers.IO) {
            try {
                val response = api.obtenerPorEstado(estado)

                if (response.isSuccessful) {
                    val solicitudes = response.body() ?: emptyList()
                    Result.success(solicitudes)
                } else {
                    Result.failure(Exception("Error: ${response.code()}"))
                }
            } catch (e: Exception) {
                Log.e("SolicitudRemoteRepo", "Error al obtener por estado: ${e.message}", e)
                Result.failure(e)
            }
        }
    }
}

package appserviciotecnico.ui.screen

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.Add
import androidx.compose.material.icons.filled.Delete
import androidx.compose.material.icons.filled.Edit
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.lifecycle.viewmodel.compose.viewModel
import appserviciotecnico.network.models.SolicitudDTO
import appserviciotecnico.network.utils.NetworkResult
import appserviciotecnico.viewmodel.SolicitudBackendViewModel

/**
 * Pantalla principal para gestionar solicitudes conectadas al backend
 * Muestra lista de solicitudes y permite crear, editar y eliminar
 */
@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun SolicitudBackendScreen(
    viewModel: SolicitudBackendViewModel = viewModel()
) {
    val solicitudesState by viewModel.solicitudesState.collectAsState()
    val crearState by viewModel.crearSolicitudState.collectAsState()
    val eliminarState by viewModel.eliminarState.collectAsState()

    var showDialog by remember { mutableStateOf(false) }
    var solicitudAEditar by remember { mutableStateOf<SolicitudDTO?>(null) }

    // Cargar solicitudes al inicio
    LaunchedEffect(Unit) {
        viewModel.obtenerSolicitudes()
    }

    // Mostrar mensaje de éxito al crear
    LaunchedEffect(crearState) {
        if (crearState is NetworkResult.Success) {
            viewModel.resetCrearState()
            showDialog = false
        }
    }

    // Mostrar mensaje de éxito al eliminar
    LaunchedEffect(eliminarState) {
        if (eliminarState is NetworkResult.Success) {
            viewModel.resetEliminarState()
        }
    }

    Scaffold(
        topBar = {
            TopAppBar(
                title = { Text("Gestión de Solicitudes (Backend)") },
                colors = TopAppBarDefaults.topAppBarColors(
                    containerColor = MaterialTheme.colorScheme.primary,
                    titleContentColor = MaterialTheme.colorScheme.onPrimary
                )
            )
        },
        floatingActionButton = {
            FloatingActionButton(
                onClick = {
                    solicitudAEditar = null
                    showDialog = true
                }
            ) {
                Icon(Icons.Default.Add, contentDescription = "Crear Solicitud")
            }
        }
    ) { padding ->
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(padding)
                .padding(16.dp)
        ) {
            // Indicador de estado
            when (solicitudesState) {
                is NetworkResult.Loading -> {
                    Box(
                        modifier = Modifier.fillMaxSize(),
                        contentAlignment = Alignment.Center
                    ) {
                        CircularProgressIndicator()
                    }
                }

                is NetworkResult.Success -> {
                    val solicitudes = (solicitudesState as NetworkResult.Success).data

                    if (solicitudes.isEmpty()) {
                        Box(
                            modifier = Modifier.fillMaxSize(),
                            contentAlignment = Alignment.Center
                        ) {
                            Text(
                                "No hay solicitudes. Crea una nueva.",
                                style = MaterialTheme.typography.bodyLarge
                            )
                        }
                    } else {
                        LazyColumn(
                            verticalArrangement = Arrangement.spacedBy(8.dp)
                        ) {
                            items(solicitudes) { solicitud ->
                                SolicitudCard(
                                    solicitud = solicitud,
                                    onEdit = {
                                        solicitudAEditar = solicitud
                                        showDialog = true
                                    },
                                    onDelete = {
                                        solicitud.id?.let { viewModel.eliminarSolicitud(it) }
                                    }
                                )
                            }
                        }
                    }
                }

                is NetworkResult.Error -> {
                    val error = (solicitudesState as NetworkResult.Error).message

                    Column(
                        modifier = Modifier.fillMaxSize(),
                        horizontalAlignment = Alignment.CenterHorizontally,
                        verticalArrangement = Arrangement.Center
                    ) {
                        Text(
                            "Error: $error",
                            color = MaterialTheme.colorScheme.error,
                            style = MaterialTheme.typography.bodyLarge
                        )
                        Spacer(modifier = Modifier.height(16.dp))
                        Button(onClick = { viewModel.obtenerSolicitudes() }) {
                            Text("Reintentar")
                        }
                    }
                }

                is NetworkResult.Idle -> {
                    // Estado inicial
                }
            }
        }

        // Diálogo para crear/editar solicitud
        if (showDialog) {
            SolicitudFormDialog(
                solicitud = solicitudAEditar,
                onDismiss = { showDialog = false },
                onSave = { solicitud ->
                    if (solicitudAEditar == null) {
                        viewModel.crearSolicitud(solicitud)
                    } else {
                        solicitud.id?.let { viewModel.actualizarSolicitud(it, solicitud) }
                    }
                }
            )
        }
    }
}

/**
 * Card para mostrar cada solicitud
 */
@Composable
fun SolicitudCard(
    solicitud: SolicitudDTO,
    onEdit: () -> Unit,
    onDelete: () -> Unit
) {
    Card(
        modifier = Modifier.fillMaxWidth(),
        elevation = CardDefaults.cardElevation(4.dp)
    ) {
        Column(
            modifier = Modifier.padding(16.dp)
        ) {
            Text(
                text = solicitud.servicio,
                style = MaterialTheme.typography.titleMedium,
                fontWeight = FontWeight.Bold
            )
            Spacer(modifier = Modifier.height(4.dp))
            Text("Cliente: ${solicitud.clienteNombre}")
            Text("Fecha: ${solicitud.fechaAgendada} - ${solicitud.horaAgendada}")
            Text("Estado: ${solicitud.estado}")
            Text("Descripción: ${solicitud.descripcion}")

            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.End
            ) {
                IconButton(onClick = onEdit) {
                    Icon(Icons.Default.Edit, contentDescription = "Editar")
                }
                IconButton(onClick = onDelete) {
                    Icon(
                        Icons.Default.Delete,
                        contentDescription = "Eliminar",
                        tint = MaterialTheme.colorScheme.error
                    )
                }
            }
        }
    }
}

/**
 * Diálogo para crear o editar una solicitud
 */
@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun SolicitudFormDialog(
    solicitud: SolicitudDTO?,
    onDismiss: () -> Unit,
    onSave: (SolicitudDTO) -> Unit
) {
    var servicio by remember { mutableStateOf(solicitud?.servicio ?: "") }
    var clienteNombre by remember { mutableStateOf(solicitud?.clienteNombre ?: "") }
    var descripcion by remember { mutableStateOf(solicitud?.descripcion ?: "") }
    var fechaAgendada by remember { mutableStateOf(solicitud?.fechaAgendada ?: "") }
    var horaAgendada by remember { mutableStateOf(solicitud?.horaAgendada ?: "") }
    var estado by remember { mutableStateOf(solicitud?.estado ?: "PENDIENTE") }
    var categoriaId by remember { mutableStateOf(solicitud?.categoriaId?.toString() ?: "1") }

    val estados = listOf("PENDIENTE", "EN_PROCESO", "COMPLETADO", "CANCELADO")
    var expandedEstado by remember { mutableStateOf(false) }

    AlertDialog(
        onDismissRequest = onDismiss,
        title = {
            Text(if (solicitud == null) "Nueva Solicitud" else "Editar Solicitud")
        },
        text = {
            Column(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(8.dp),
                verticalArrangement = Arrangement.spacedBy(8.dp)
            ) {
                OutlinedTextField(
                    value = servicio,
                    onValueChange = { servicio = it },
                    label = { Text("Servicio") },
                    modifier = Modifier.fillMaxWidth()
                )
                OutlinedTextField(
                    value = clienteNombre,
                    onValueChange = { clienteNombre = it },
                    label = { Text("Cliente") },
                    modifier = Modifier.fillMaxWidth()
                )
                OutlinedTextField(
                    value = descripcion,
                    onValueChange = { descripcion = it },
                    label = { Text("Descripción") },
                    modifier = Modifier.fillMaxWidth(),
                    maxLines = 3
                )
                OutlinedTextField(
                    value = fechaAgendada,
                    onValueChange = { fechaAgendada = it },
                    label = { Text("Fecha (YYYY-MM-DD)") },
                    modifier = Modifier.fillMaxWidth()
                )
                OutlinedTextField(
                    value = horaAgendada,
                    onValueChange = { horaAgendada = it },
                    label = { Text("Hora (HH:mm)") },
                    modifier = Modifier.fillMaxWidth()
                )

                ExposedDropdownMenuBox(
                    expanded = expandedEstado,
                    onExpandedChange = { expandedEstado = !expandedEstado }
                ) {
                    OutlinedTextField(
                        value = estado,
                        onValueChange = {},
                        readOnly = true,
                        label = { Text("Estado") },
                        trailingIcon = {
                            ExposedDropdownMenuDefaults.TrailingIcon(expanded = expandedEstado)
                        },
                        modifier = Modifier
                            .fillMaxWidth()
                            .menuAnchor()
                    )
                    ExposedDropdownMenu(
                        expanded = expandedEstado,
                        onDismissRequest = { expandedEstado = false }
                    ) {
                        estados.forEach { item ->
                            DropdownMenuItem(
                                text = { Text(item) },
                                onClick = {
                                    estado = item
                                    expandedEstado = false
                                }
                            )
                        }
                    }
                }

                OutlinedTextField(
                    value = categoriaId,
                    onValueChange = { categoriaId = it },
                    label = { Text("Categoría ID") },
                    modifier = Modifier.fillMaxWidth()
                )
            }
        },
        confirmButton = {
            Button(
                onClick = {
                    val nuevaSolicitud = SolicitudDTO(
                        id = solicitud?.id,
                        servicio = servicio,
                        clienteNombre = clienteNombre,
                        descripcion = descripcion,
                        fechaAgendada = fechaAgendada,
                        horaAgendada = horaAgendada,
                        estado = estado,
                        categoriaId = categoriaId.toIntOrNull() ?: 1
                    )
                    onSave(nuevaSolicitud)
                },
                enabled = servicio.isNotBlank() && clienteNombre.isNotBlank()
            ) {
                Text("Guardar")
            }
        },
        dismissButton = {
            TextButton(onClick = onDismiss) {
                Text("Cancelar")
            }
        }
    )
}

package appserviciotecnico.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import appserviciotecnico.network.models.SolicitudDTO
import appserviciotecnico.network.repository.SolicitudRemoteRepository
import appserviciotecnico.network.utils.NetworkResult
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.launch

/**
 * ViewModel: Gestiona el estado de las solicitudes y la comunicación con el backend
 * Expone estados reactivos (StateFlow) para que la UI los observe
 */
class SolicitudBackendViewModel : ViewModel() {

    private val repository = SolicitudRemoteRepository()

    // Estado de la lista de solicitudes
    private val _solicitudesState = MutableStateFlow<NetworkResult<List<SolicitudDTO>>>(NetworkResult.Idle)
    val solicitudesState: StateFlow<NetworkResult<List<SolicitudDTO>>> = _solicitudesState.asStateFlow()

    // Estado de creación de solicitud
    private val _crearSolicitudState = MutableStateFlow<NetworkResult<SolicitudDTO>>(NetworkResult.Idle)
    val crearSolicitudState: StateFlow<NetworkResult<SolicitudDTO>> = _crearSolicitudState.asStateFlow()

    // Estado de actualización
    private val _actualizarState = MutableStateFlow<NetworkResult<SolicitudDTO>>(NetworkResult.Idle)
    val actualizarState: StateFlow<NetworkResult<SolicitudDTO>> = _actualizarState.asStateFlow()

    // Estado de eliminación
    private val _eliminarState = MutableStateFlow<NetworkResult<Unit>>(NetworkResult.Idle)
    val eliminarState: StateFlow<NetworkResult<Unit>> = _eliminarState.asStateFlow()

    /**
     * Obtener todas las solicitudes del backend
     */
    fun obtenerSolicitudes() {
        viewModelScope.launch {
            _solicitudesState.value = NetworkResult.Loading

            val result = repository.obtenerSolicitudes()

            _solicitudesState.value = result.fold(
                onSuccess = { NetworkResult.Success(it) },
                onFailure = { NetworkResult.Error(it.message ?: "Error desconocido", it as? Exception) }
            )
        }
    }

    /**
     * Crear nueva solicitud
     */
    fun crearSolicitud(solicitud: SolicitudDTO) {
        viewModelScope.launch {
            _crearSolicitudState.value = NetworkResult.Loading

            val result = repository.crearSolicitud(solicitud)

            _crearSolicitudState.value = result.fold(
                onSuccess = { NetworkResult.Success(it) },
                onFailure = { NetworkResult.Error(it.message ?: "Error al crear", it as? Exception) }
            )

            // Recargar la lista después de crear
            if (result.isSuccess) {
                obtenerSolicitudes()
            }
        }
    }

    /**
     * Actualizar solicitud existente
     */
    fun actualizarSolicitud(id: Long, solicitud: SolicitudDTO) {
        viewModelScope.launch {
            _actualizarState.value = NetworkResult.Loading

            val result = repository.actualizarSolicitud(id, solicitud)

            _actualizarState.value = result.fold(
                onSuccess = { NetworkResult.Success(it) },
                onFailure = { NetworkResult.Error(it.message ?: "Error al actualizar", it as? Exception) }
            )

            // Recargar la lista después de actualizar
            if (result.isSuccess) {
                obtenerSolicitudes()
            }
        }
    }

    /**
     * Eliminar solicitud
     */
    fun eliminarSolicitud(id: Long) {
        viewModelScope.launch {
            _eliminarState.value = NetworkResult.Loading

            val result = repository.eliminarSolicitud(id)

            _eliminarState.value = result.fold(
                onSuccess = { NetworkResult.Success(Unit) },
                onFailure = { NetworkResult.Error(it.message ?: "Error al eliminar", it as? Exception) }
            )

            // Recargar la lista después de eliminar
            if (result.isSuccess) {
                obtenerSolicitudes()
            }
        }
    }

    /**
     * Obtener solicitudes por estado
     */
    fun obtenerPorEstado(estado: String) {
        viewModelScope.launch {
            _solicitudesState.value = NetworkResult.Loading

            val result = repository.obtenerPorEstado(estado)

            _solicitudesState.value = result.fold(
                onSuccess = { NetworkResult.Success(it) },
                onFailure = { NetworkResult.Error(it.message ?: "Error", it as? Exception) }
            )
        }
    }

    /**
     * Resetear estados
     */
    fun resetCrearState() {
        _crearSolicitudState.value = NetworkResult.Idle
    }

    fun resetActualizarState() {
        _actualizarState.value = NetworkResult.Idle
    }

    fun resetEliminarState() {
        _eliminarState.value = NetworkResult.Idle
    }
}
